//
// Static initializers
//

int HersheyFont::defaultFontIndex = 0;

HersheyFont::HersheyFont()
{
    fontIndex = defaultFontIndex;
};

HersheyFont::HersheyFont(const HersheyFont& H)
{
    fontIndex = H.fontIndex;
}

HersheyFont::~HersheyFont()
{};


void  HersheyFont::setFont(const std::string& fontName)
{
    int k;

    fontIndex = defaultFontIndex;

    for(k = 0; k < 21; k++)
    {
    if(fontName == HersheyFontInfo[k].name)
    {fontIndex = k;}
    }

};

void  HersheyFont::setFont(int fIndex)
{
    if((fIndex < 0 )|| (fIndex > 21)) fIndex = defaultFontIndex;
    fontIndex = fIndex;
};

std::string HersheyFont::getFontName()
{
    return HersheyFontInfo[fontIndex].name;
};



double HersheyFont::getHersheyHeight() 
{ 
    return HERSHEY_HEIGHT;
}   

double HersheyFont::getHersheyWidth(const std::string& textString)
{
    int   i;
    int   index; 
    int   glyphIndex;
    char* codeString;

    double  sWidth = 0;

    for(i = 0; i < (int)textString.size(); i++)
    {
    index       =  int(textString[i]);
    glyphIndex  =  HersheyFontInfo[fontIndex].chars[index];
    codeString  =  (char* const)((void*)OccidentalHersheyGlyphs[glyphIndex]);
    sWidth     +=  double(codeString[1] - codeString[0]);
    }
    return sWidth;
}

char* const HersheyFont::getGlyphCodeString(char c)
{
    int index       = int(c);
    int glyphIndex  = HersheyFontInfo[fontIndex].chars[index];
    return (char* const)((void*)OccidentalHersheyGlyphs[glyphIndex]);
}

/* 
   Dimensions for characters in principal [large] size.  The `centerline'
   is located at y=0, in the original coordinate system.  The `topline' and
   `bottomline' are determined by the tallest characters, which are
   parentheses, brackets, and braces. 
*/

double HersheyFont::HERSHEY_BASELINE    =  -9.5;	/* relative to centerline */
double HersheyFont::HERSHEY_CAPLINE     =  12.5;	/* relative to centerline */
double HersheyFont::HERSHEY_TOPLINE     =  16.5;	/* relative to centerline */
double HersheyFont::HERSHEY_BOTTOMLINE  = -16.5;    /* relative to centerline */

double HersheyFont::HERSHEY_CAPHEIGHT   =  22;      /* i.e. capline - baseline */
double HersheyFont::HERSHEY_ASCENT      =  26;      /* i.e. topline - baseline */
double HersheyFont::HERSHEY_DESCENT     =   7;	    /* i.e. baseline - bottomline */
double HersheyFont::HERSHEY_HEIGHT      = (HERSHEY_ASCENT + HERSHEY_DESCENT);
double HersheyFont::HERSHEY_EM          =  33;



